INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (413027, 'Desmond Harrington', '1976-10-19', null, 'Desmond Harrington (born October 19, 1976) is an American actor. He is known for movies such as The Hole, Wrong Turn and Ghost Ship. He joined the cast of the Showtime series Dexter in its third season as Joey Quinn.
