INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (9567, 'Charlotte Rampling', '1946-02-05', null, 'Charlotte Rampling, OBE (born Tessa Charlotte Rampling; 5 February 1946) is an English actress. Her career spans four decades in English-language as well as French and Italian cinema.');
