INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (422454, 'Kristen Connolly', '1980-07-12', null, 'Kristen Connolly is an American actress. She is known for her roles as Christina Gallagher on the Netflix show House of Cards and as Dana Polk in the 2012 movie The Cabin in the Woods.');
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (435847, 'Michael Gill', '1960-04-16', null, 'Michel Gill (born April 16, 1960), also known as Michael Gill, is an American actor best known for playing President Garrett Walker in the Netflix series House of Cards. Gill was born in New York City, New York. He studied at Aiglon College, a prestigious boarding school in Switzerland. After Aiglon, he attended Tufts University before transferring and graduating from the Juilliard School in 1985 (Group 14).');
