INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (446429, 'Candice Patton', '1988-06-24', null, 'Raised in Plano, Texas, Candice continued her childhood dream of becoming an actress by attending Southern Methodist University in Dallas where she graduated Summa Cum Laude with a B.F.A in Theatre. It was here that she was scouted by The Young and the Restless for a CBS Soap Star Contest and flew out to Los Angeles to briefly join the cast. Candice finished her college education at SMU and moved to Los Angeles shortly after.');
