INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (18867, 'Colton Haynes', '1988-07-13', null, 'Colton Haynes is an American actor and model. At the age of fifteen, Haynes began modeling for Abercrombie &amp; Fitch while living in New York City, New York. After moving to Los Angeles, California, he began acting in television series such as CSI: Miami and Pushing Daisies and Arrow. He is best known for his role as Jackson Whittemore in MTV''s supernatural drama series Teen Wolf.');
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (412257, 'Wentworth Miller', '1972-06-02', null, 'Wentworth Earl Miller III (born June 2, 1972) is an English-born American actor, model, and screenwriter who rose to stardom following his role as Michael Scofield in the Fox Network television series Prison Break.');
