INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (449856, 'Camren Bicondova', '1999-05-22', null, 'Camren Renee Bicondova (born May 22, 1999) is an American dancer and child actress and part of the all-girl dance group 8 Flavahz. Born in San Diego, California, she first came to prominence when she was discovered by Chris Stokes and featured in his musical-drama Battlefield America.
