INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (446978, 'Kylie Bunbury', '1989-01-30', null, 'Bunbury was born in Hamilton, Ontario to a Polish and Swedish American mother, Kristi Novak, and Guyanese-born Canadian Alex Bunbury. Kylie''s father was a professional soccer player who won the sixth most caps all time for the Canadian men''s soccer team. She has two younger brothers, Tealand Logan. Bunbury originally worked as a model, but then her agency suggested she try acting. She landed her first role as Kathleen in Days of Our Lives for one episode. She also had roles in Prom and The Sitter. On February 26, 2015, it was announced that Bunbury would portray the role of Eva in the science fiction CBS television series Under the Dome.');
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (428537, 'Greg Grunberg', '1966-07-11', null, 'Gregory Phillip "Greg" Grunberg (born July 11, 1966) is an American television actor. He is best known from starring as Matt Parkman on the NBC television series Heroes. Other notable roles included the characters Sean Blumberg on Felicity (1998–2002) and Eric Weiss on Alias (2001–2006), both created and produced by childhood friend J. J. Abrams.
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (417134, 'Ali Larter', '1976-02-28', null, 'Alison Elizabeth "Ali" Larter (born February 28, 1976) is an American actress best known for playing the dual roles of Niki Sanders and Tracy Strauss on the NBC science fiction drama Heroes.
