INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (463994, 'Carrie Coon', '1981-01-24', null, 'Carrie Alexandra Coon (born January 24, 1981) is an American actress. Coon was nominated for a Tony Award for her performance in the 2012 revival of Who''s Afraid of Virginia Woolf?. In 2014, she began starring as Nora Durst in the HBO drama series, The Leftovers, and appeared in the thriller film Gone Girl.');
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (7861, 'Milo Ventimiglia', '1977-07-08', null, 'Milo Anthony Ventimiglia (born July 8, 1977) is an American television actor best known for his role of Peter Petrelli on the TV series Heroes. Ventimiglia was first noticed by fans during his three year stint on the WB series Gilmore Girls, playing Rory Gilmore''s boyfriend Jess Mariano from 2001 until his final guest appearance in 2006.
