INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (426128, 'James Kyson', '1975-12-13', null, 'James Kyson Lee (born December 13, 1975) is a Korean-born American actor best known for his role of Ando Masahashi on the NBC television series Heroes.
