INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (440565, 'Ellie Kemper', '1980-05-02', null, 'Elizabeth Claire "Ellie" Kemper (born May 2, 1980) is an American actress, comedian, and writer. She is best known for her role as Erin Hannon in the NBC series The Office, as well as her supporting roles in the films Bridesmaids and 21 Jump Street. She is currently attached to star in the upcoming NBC comedy Unbreakable Kimmy Schmidt, created by Tina Fey.');
