INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (431554, 'Josie Loren', '1987-03-19', null, 'Josie Loren Lopez (born March 19, 1987), known professionally as Josie Loren, is an American actress. She is best known for the role of Kaylie Cruz in the ABC Family series Make It or Break It, and as FBI Agent Michelle Vega in the TV series The Mentalist.');
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (13850, 'Ewan McGregor', '1971-03-31', null, 'Ewan Gordon McGregor was born 31 March 1971 in Crieff, Scotland. At 16, he left Crieff and Morrison Academy to join the Perth Repertory Theatre. His parents encouraged him to leave school and pursue his acting goals rather than be unhappy. McGregor studied drama for a year at Kirkcaldly in Fife, then enrolled at London''s Guildhall School of Music and Drama for a three-year course. He studied alongside Daniel Craig and Alistair McGowan among others, and left right before graduating after snagging the role of Private Mick Hopper in Dennis Potter''s 1993 six-part Channel 4 series "Lipstick on Your Collar" (1993).
