INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (453276, 'Clara Paget', '1988-09-12', null, 'Lady Clara Elizabeth Iris Paget (born 12 September 1988) is an English model and actress known for her roles in films St Trinian''s 2: The Legend of Fritton''s Gold and Fast & Furious 6 and her role as Anne Bonny in the television series Black Sails.');
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (10951, 'Mary Elizabeth Winstead', '1984-11-28', null, 'Mary Elizabeth Winstead (born November 28, 1984) is an American actress. Winstead has primarily been called a scream queen because of her roles in the horror films Final Destination 3, Black Christmas, Death Proof, and The Thing. She has also branched out into other genre films Sky High, Bobby, Live Free or Die Hard, Make It Happen, and Scott Pilgrim vs. the World. She was nominated for a Young Artist Award for her performance in Passions, and received a Screen Actors Guild Award nomination as part of the cast of Bobby.
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (425856, 'Shea Whigham', '1969-01-05', null, 'Franklin Shea Whigham, Jr. (born January 5, 1969), best known as Shea Whigham, is an American actor currently starring as Elias Thompson on the HBO dramatic series Boardwalk Empire.
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (6466, 'David Thewlis', '1963-03-20', null, '​David Thewlis (born 20 March 1963) is an English film, television and stage character actor, as well as a writer.
