INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (430516, 'Ingrid Bolsø Berdal', '1980-03-02', null, 'Ingrid Bolsø Berdal (born 2 March 1980 in Utøy, Inderøy) is a Norwegian actress. She studied music and singing in high school, and after graduating she continued her music education at the University of Trondheim (NTNU), studying jazz singing and improvisation for two years. She moved to Oslo and was accepted at Oslo National Academy of Dramatic Arts (KHiO), where she studied acting for three years.
