INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (450499, 'Kumail Nanjiani', '1978-02-21', null, 'Kumail Nanjiani is a Pakistani-American stand-up comedian, film and television actor, and podcast host, best known as main cast member of the television series "Silicon Valley", as well as for providing the voice of Prismo on the animated series "Adventure Time". He also co-hosts the Comedy Central show "The Meltdown with Jonah and Kumail", and two podcasts: "The Indoor Kids" and "The X-Files Files".');
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (16009, 'Martin Starr', '1982-07-30', null, 'Martin Starr (born Martin James Pflieger Schienle; July 30, 1982) is an American television and film actor, known for his roles as Bill Haverchuck on the short-lived NBC comedy-drama Freaks and Geeks and as Roman DeBeers on the Starz comedy series Party Down, as well as for his roles in the films Knocked Up, Cheats, and Adventureland.
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (423285, 'Amanda Crew', '1986-06-05', null, 'Amanda Crew (born June 5, 1986) is a Canadian television and film actress. She is best known for her work on the television series 15/Love and Whistler, and in the feature-length films Final Destination 3, Sex Drive, The Haunting in Connecticut, and Charlie St. Cloud.');
