INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (422129, 'Harry Shum Jr.', '1982-04-28', null, 'Harry Shum Jr. was born on April 28, 1982 in Puerto Limón, Costa Rica. He is an actor, known for Glee (2009), Street Dance (2008) and Crouching Tiger, Hidden Dragon: Sword of Destiny (2016). He has been married to Shelby Rabara since November 22, 2015. Height 5'' 11" (1,8 m). He speaks English, Spanish and Chinese. Has two older sisters, Susana and Cristina. He was born in Puerto Limón, Costa Rica, to Chinese parents. His father is from Guangzhou and his mother is from Hong Kong.');
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (14005, 'Jeff Daniels', '1955-02-19', null, 'Jeffrey Warren "Jeff" Daniels (born February 19, 1955) is an American actor, musician and playwright. He founded a non-profit theatre company, the Purple Rose Theatre Company, in his home state of Michigan. He has performed in a number of stage productions, both on and off Broadway. He has been nominated for the Tony Award as Best Actor for the Broadway play God of Carnage (2009), along with his other three cast-mates.
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (13133, 'Emily Mortimer', '1971-12-01', null, 'Emily Kathleen A. Mortimer (born 1 December 1971) is an English actress. She began performing on stage, and has since appeared in several film and television roles, including Scream 3, Match Point, Lars and the Real Girl, and Shutter Island.');
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (415989, 'John Gallagher Jr.', '1984-06-17', null, 'John Howard Gallagher, Jr. (born June 17, 1984) is a Tony award-winning American actor and musician known for originating the role of Moritz Stiefel in Duncan Sheik and Steven Sater''s rock musical Spring Awakening. He also played Johnny aka "Jesus of Suburbia" in Green Day''s Broadway musical, American Idiot and Lee in the 2011 Broadway production of Jerusalem. As of 2012[update], he plays Jim Harper in Aaron Sorkin''s The Newsroom.');
