INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (298545, 'Tuppence Middleton', '1987-02-21', null, 'Tuppence Middleton was born on 21 February 1987 in Bristol, England to Nigel and Tina Middleton. She was named "Tuppence" after her mother''s childhood nickname given to her by her mother. She was raised in Clevedon in Somerset, with younger brother Josh, and older sister Angel.
