INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (416242, 'Chris Zylka', '1985-05-09', null, 'Chris Zylka (born May 9, 1985) is an American actor and model. He has played a recurring role as Gabe Lammotti on Disney Channel''s Hannah Montana. Zylka appeared in the Dangerous Muse music video for "The Rejection." Zylka will portray Flash Thompson in the upcoming Spider-Man reboot.');
