INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (400173, 'Joseph Marcell', '1948-08-18', null, 'Joseph Marcell (born 18 August 1948) is a St Lucian born, British actor, best known for his role as Geoffrey the Englishbutler on the NBC sitcom The Fresh Prince of Bel-Air from its beginning in September 1990, until its final episode in May 1996. Born in Saint Lucia, he moved to England at the age of nine, and currently resides in Southall, Ealing, in London.');
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (6234, 'Ioan Gruffudd', '1973-10-06', null, 'Ioan Gruffudd is a Welsh actor who trained at the Royal Academy of Dramatic Art in London, England. He started off mainly in Welsh-language productions, then came to international attention as Fifth Officer Harold Lowe in the film "Titanic" (1997), and as Lt. John Beales in "Black Hawk Down" (2001).
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (297340, 'Judd Hirsch', '1935-03-15', null, 'Judd Hirsch (born March 15, 1935) is an American actor most known for playing Alex Rieger on the television comedy series Taxi, John Lacey on the NBC series Dear John, and Alan Eppes on the CBS series Numb3rs.');
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (432398, 'Alana de la Garza', '1976-06-18', null, 'Alana De La Garza was born in Columbus, Ohio and later relocated to Texas, ultimately attending the University of Texas. After college, she moved to Orlando, Florida, landing roles in a number of independent films as well as commercials. Her work took her to New York, where she studied extensively under renowned acting instructor JoAnna Beckson. After getting her break as "Rosa Santos" on the daytime drama "All My Children" (1970), Alana moved to Los Angeles. A slew of guest spots followed. One of the most recognizable was her role as a con artist on the hit show "Las Vegas" (2003), followed by a series lead on the WB''s "The Mountain" (2004). She recently completed the romantic comedy Mr. Fix It (2006) opposite David Boreanaz and has been guesting on "CSI: Miami" (2002), before joining the cast of "Law &amp; Order" (1990) in June 2006.');
