INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (430555, 'Peyton List', '1986-08-08', null, 'Peyton List is an American actress and model, known for playing Jane Sterling on the AMC series Mad Men and Nicole Kirby on the ABC series FlashForward. In 1996, Peyton made her first big screen appearance in Washington Square. While working on As the World Turns, List was offered the small but significant role of Sarah Wallis in Bill Paxton''s The Greatest Game Ever Played. The 2005 movie was a critical and box-office success. In 2008, she starred in two independent movies - action movie Deep Winter and the horror thriller Shuttle.');
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (411139, 'Jorja Fox', '1968-07-07', null, 'Jorja-An Fox (born July 7, 1968, in New York City, New York height 5'' 8½" (1,74 m) ), is an American actress, musician and songwriter. She first came to prominence as a guest star on the television drama ER, portraying the recurring role of Dr. Maggie Doyle from 1996 to 1999. This was followed by another critical success in the recurring role of Secret Service Agent Gina Toscano on The West Wing in 2000.
