INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (422829, 'Emory Cohen', '1990-03-13', null, 'Emory Isaac Cohen (born March 13, 1990) is an American actor. He made his feature film debut in Afterschool (2008). He is best known for his roles as AJ Cross in Derek Cianfrance''s film The Place Beyond the Pines (2012), Tony Fiorello in John Crowley''s film Brooklyn (2015) and Homer in the Netflix series The OA (2016).');
