INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (134192, 'Henderson Wade', null, null, 'Henderson is a Washington, DC native and graduate of Syracuse University''s Newhouse School. He began studying with Chick Vennera in 2008 and is presently the Managing Director at The Renegade Theatre and Film Group. His television credits to date include Criminal Minds: Suspect Behavior, House M.D., Big Love, CSI: Miami and Days of Our Lives among others. In addition, Henderson has also acted in several independent films; one of which, The Bleeding House, was an official selection of the Tribeca Film Festival in 2011. SAG/AFTRA');
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (466379, 'Kiersey Clemons', '1993-12-17', null, 'Kiersey Nicole Clemons (born December 17, 1993) is an American actress and singer. Clemons is best known for portraying Cassandra "Diggy" Andrews in the acclaimed 2015 comedy-drama film Dope. Her other notable roles include Transparent, Extant and Neighbors 2: Sorority Rising. Clemons is cast as Iris West in the DC Extended Universe.');
