INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (414786, 'Joanne Whalley', '1961-08-25', null, 'Joanne Whalley (born 25 August 1961) is an English actress who began her career in 1974. She has since amassed numerous credits, primarily on television, but also in nearly 30 feature films, including Dance with a Stranger (1985), Willow (1988), Scandal (1989), The Secret Rapture (1993) and Mother''s Boys (1994). Following her marriage to Val Kilmer in 1988, she was credited as Joanne Whalley-Kilmer until their divorce in 1996.
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (431312, 'Holliday Grainger', '1988-03-27', null, 'Holliday Clark Grainger (also credited as Hollie Grainger; born 27 March 1988) is an English screen and stage actress. Some of her prominent roles include Lucrezia Borgia in Showtime series The Borgias and Estella in Mike Newell''s Great Expectations. In May 2010, Grainger was named one of the 55 faces of the future in Nylon magazine''s Young Hollywood Issue.
