INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (419970, 'Jeremy Clarkson', '1960-04-11', null, 'Jeremy Charles Robert Clarkson (born 11 April 1960) is an English broadcaster, journalist and writer who specialises in motoring. He is best known for his role on the BBC TV show Top Gear along with co-presenters Richard Hammond and James May. He also writes weekly columns for The Sunday Times and The Sun.
