INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (534731, 'Keith Hamilton Cobb', '1962-01-28', null, 'Keith Hamilton Cobb Graduated Sleepy Hollow High School and Westchester  Community College. As well as New York University Tisch School of the  Arts. He attended Circle in the Square Theatre School and Playwrights  Horizons Theater School. His hobbies are writing, photography &amp;  renovating home. He lives in Los Angeles, California, USA. He has two  sisters, Pamela and Lane Cobb, both of whom are physicians. His mother,  Mary Lane Cobb is a retired physician and father James Cobb is a  mechanical engineer. Keith has often said that his biggest inspiration  are his hard working African American parents.');
