INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (14597, 'Gael García Bernal', '1978-11-30', null, '​García Bernal was born in Guadalajara, Mexico, the son of Patricia Bernal, an actress and former model, and José Angel García, an actor and director. His stepfather is Sergio Yazbek, whom his mother married when García Bernal was young. He started acting at just a year old and spent most of his teen years starring in telenovelas. Gael studied the International Baccalaureate, with chemistry being unquestionably his favorite subject. When he was fourteen, he taught indigenous peoples in Mexico to read, often working with the Huichol Indians. In his later teens, he took part in peaceful demonstrations during the Chiapas uprisingof 1994.');
INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (337006, 'Lola Kirke', '1990-09-27', null, 'Lola Kirke is a British-born American actress and singer-songwriter, best known for her leading roles in the feature film "Mistress America" and the television series "Mozart in the Jungle", as well as her supporting role in David Fincher''s feature film "Gone Girl". She''s a graduate from Bard College, New York, USA.');
