INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (299438, 'Daws Butler', '1916-11-16', '1988-05-18', 'Charles Dawson "Daws" Butler was a voice actor originally from Toledo, Ohio. He worked mostly for Hanna-Barbera and originated the voices of many famous animated cartoon characters, including Yogi Bear, Quick Draw McGraw, Snagglepuss, and Huckleberry Hound.');
