INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (461199, 'Nicholas Lyndhurst', '1961-04-20', null, 'Nicholas Simon Lyndhurst is an English actor. He is known for playing Rodney Trotter in Only Fools and Horses, Gary Sparrow in Goodnight Sweetheart, Dan Griffin in the BBC drama New Tricks and Adam Parkinson in Carla Lane''s series Butterflies. Lyndhurst also prominently starred as Ashley Philips in The Two of Us, as Fletch''s son Raymond in Going Straight, the sequel to the classic British sitcom Porridge, Jimmy Venables in After You''ve Gone, and Freddie ''The Frog'' Robdal in the Only Fools and Horses prequel Rock & Chips.');
