INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (453515, 'Amy Schumer', '1981-06-01', null, 'Amy Schumer was born in Manhattan and grew up on Long Island, New York. She attended South Side High School and was voted both "Class Clown" and "Teacher''s Worst Nightmare" upon graduation in 1999. Schumer graduated from Towson University in 2003 with a degree in theater. She moved to New York City after college, where she studied at the William Esper Studio for two years and worked as a bartender and a waitress. Through her father, she is the niece of Chuck Schumer, U.S. Senator from New York.
