INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (445179, 'Nina Arianda', '1984-09-18', null, 'Nina Arianda (born Nina Arianda Matijcio; September 18, 1984) is an American film, stage and television actress. She won a Tony Award for Best Lead Actress in a Play in 2012 for Venus in Fur. She was also nominated for another Tony for her performance in Born Yesterday.');
