INSERT INTO public.actors (id, name, birthday, death, biography) VALUES (430192, 'Jim Norton', '1968-07-19', null, 'James Joseph "Jim" Norton, Jr. (b July 19, 1968) is an American comedian, radio personality, author, and actor. He is an on-air personality, often dubbed the ''third mic'' on The Opie &amp; Anthony Show, which airs on Sirius XM Radio. Norton has appeared on The Tonight Show with Jay Leno and on the Late Show with David Letterman, filmed an HBO stand-up special for the series One Night Stand as well as his special "Monster Rain", co-starred in the short-lived HBO sitcom Lucky Louie and had cameos in the films Spider-Man, Zack and Miri Make a Porno and Courting Condi.');
